StructuredTextModel.Text:
///Status extraction///
	tmp_Status.ch := UINT_TO_WORD( Status);
	DriveStatus.DuringRun	:=	tmp_Status.bit[0];
	DriveStatus.DuringZeroSpeed	:=	tmp_Status.bit[1];
	DriveStatus.ReverseRunning	:=	tmp_Status.bit[2];
	DriveStatus.DuringFaultResetSignalInput	:=	tmp_Status.bit[3];
	DriveStatus.DuringSpeedAgree	:=	tmp_Status.bit[4];
	DriveStatus.DuringDriveReady	:=	tmp_Status.bit[5];
	DriveStatus.DuringAlarm	:=	tmp_Status.bit[6];
	DriveStatus.DuringFault	:=	tmp_Status.bit[7];
	DriveStatus.DuringOperationError	:=	tmp_Status.bit[8];
	DriveStatus.DuringMomentaryPowerLoss	:=	tmp_Status.bit[9];
	DriveStatus.NetCtrlStatus	:=	tmp_Status.bit[10];
	DriveStatus.DigitalOutput1Status	:=	tmp_Status.bit[11];
	DriveStatus.DigitalOutput2Status	:=	tmp_Status.bit[12];
	DriveStatus.DigitalOutput3Status	:=	tmp_Status.bit[13];
	DriveStatus.Motor2Selected	:=	tmp_Status.bit[14];
	DriveStatus.ZeroServoEnd	:=	tmp_Status.bit[15];
	
///AccelerationDeltaSpeed and DecelerationDeltaSpeed set to 15000///
	AccelerationDeltaSpeed:=UDINT#150000;
	DecelerationDeltaSpeed:=UDINT#150000;
	
///Command extraction///
IF Enable THEN;
	tmp_Command.bit[0] := Fwd;
	tmp_Command.bit[1] := Rev;
	Active:=TRUE;
ELSE
	tmp_Command.bit[0] := FALSE;
	tmp_Command.bit[1] := FALSE;
	Active:=FALSE;
END_IF;
	tmp_Command.bit[3] := FaultReset;
	Command := WORD_TO_UINT( tmp_Command.ch);

///Command controll///
IF Enable THEN;
	
 	//CmdFrequencyReference form a REAL value//
	FrequencyReference:=REAL_TO_UINT(CmdFrequencyReference*100);

	//AccelerationDeltaTime form a REAL value CmdAccelerationReference//
	IF CmdAccelerationReference>REAL#0.0 THEN;	//If the acceleration is 0.0s the FB will set the AccelerationDeltaTime to 10.0s
		AccelerationDeltaTime:=REAL_TO_UINT(CmdAccelerationReference*100);
	ELSE
		AccelerationDeltaTime:=UINT#1000;
	END_IF;

	//DecelerationDeltaTime form a REAL value CmdDecelerationReference//
	IF CmdDecelerationReference>REAL#0.0 THEN;	//If the acceleration is 0.0s the FB will set the AccelerationDeltaTime to 10.0s
		DecelerationDeltaTime:=REAL_TO_UINT(CmdDecelerationReference*100);
	ELSE
		DecelerationDeltaTime:=UINT#1000;
	END_IF;

	//Convert ActOutputFrequencyMonitor to a REAL value//
	ActOutputFrequencyMonitor:=(UINT_TO_REAL(OutputFrequencyMonitor))/100.0;
	
END_IF;

///Command controll///
IF Enable THEN;
	
	//0 Frequence //
	IF (CmdFrequencyReference<=REAL#0.0) and (Fwd or Rev) THEN;
		Frequency0HzWarning:=TRUE;
	ELSE
		Frequency0HzWarning:=FALSE;
	END_IF;
	
END_IF;
