StructuredTextModel.Text:
///This function is to monitor the value of the analog voltage that is input to the analog AI.///
IF Enable THEN
	
	///Monitor for AI in percentage///
		AnalogPercentage:=AnalogInput/UINT#10;			
		
	///Monitor for AI///
	IF AnalogSignalLevelSelect= eSignalLevelSelect#act_0_to_10V THEN; (*0 : 0 to 10V (Lower Limit at 0)*)
			AnalogMonitor:=ScaleTrans(SclIn:=AnalogInput, X0:=0, Y0:=0, X1:=1000, Y1:=10);
		
	ELSIF AnalogSignalLevelSelect= eSignalLevelSelect#act_0_to_10V_woLowerLimit THEN; (*1 : 0 to +10V (Without Lower Limit)*)
			AnalogMonitor:=ScaleTrans(SclIn:=AnalogInput, X0:=0, Y0:=0, X1:=1000, Y1:=10);
			
	ELSIF AnalogSignalLevelSelect= eSignalLevelSelect#act_4_to_20mA THEN; (*2 : 4 to 20 mA*)	
			AnalogMonitor:=ScaleTrans(SclIn:=AnalogInput, X0:=0, Y0:=4, X1:=1000, Y1:=20);
			
	ELSIF AnalogSignalLevelSelect= eSignalLevelSelect#act_0_to_20mA THEN; (*3 : 0 to 20 mA*)	
			AnalogMonitor:=ScaleTrans(SclIn:=AnalogInput, X0:=0, Y0:=0, X1:=1000, Y1:=20);		
	END_IF;
	
	Active:=TRUE;
		
ELSE;
	
	AnalogMonitor:=0;
	AnalogPercentage:=0;
	Active:=FALSE;
	
END_IF;
