StructuredTextModel.Text:
///FB ON///
If Enable THEN;

///Status extraction///
	tmp_Status.ch := UINT_TO_WORD(Status);
	
	IF tmp_Status.bit[6] OR	//During Alarm  
		tmp_Status.bit[7] OR	//During Fault
		tmp_Status.bit[8] OR	// During Operation Error
		_EC_LinkOffErr AND NOT _EC_EntrySlavTbl[NodeAddress]  THEN	//During slave communication error
			Error:=TRUE;
	ELSE
			Error:=FALSE;
	END_IF;

///If Q2x has an fault the latest fault will be outputted///
	IF tmp_Status.bit[6] or 
		tmp_Status.bit[7] or 
		Init THEN
	
	CASE Stage OF
		
		5:	RdExecute:=TRUE;
			Stage:=10;
			Init:=TRUE;
		
		10:	
			IF ReadSDO.Done THEN;
				RdExecute:=FALSE;
				Busy:=TRUE;
				ErrorSdo:=FALSE;
				Stage:=20;
			ELSIF ReadSDO.Error THEN;
				Stage:=1000;
			END_IF;	
		
		20://Success
		
		;	Done:=TRUE;
			Busy:=FALSE;
			ErrorSdo:=FALSE;
			ErrorID:=16#0;
			AbortCode:=16#00;
			Init:=FALSE;
		
		1000://Error
		
			Done:=FALSE;
			Busy:=FALSE;
			Error:=TRUE;
			ErrorID:=ReadSDO.ErrorID;
			AbortCode:=ReadSDO.AbortCode;
			Init:=FALSE;
			AuxData:=UDINT#0020;
			
	END_CASE;	
	
	
	ELSE
		RdExecute:=FALSE;
		Busy:=FALSE;
		Done:=FALSE;
		Init:=FALSE;
		SDOObjError.Index:=UINT#16#1003;
		SDOObjError.Subindex:=USINT#16#1;
		Stage:=5;
		AuxData:=0;

	END_IF;

///Read SDO call///
	ReadSDO(Execute:=	RdExecute,
			NodeAdr:= 	NodeAddress,
			SDOObj:= 	SDOObjError ,
			TimeOut:=	UINT#20 ,
			ReadDat :=  AuxData);

///Communication broken to the slave drive///
	IF _EC_LinkOffErr AND NOT _EC_EntrySlavTbl[NodeAddress] THEN
		AuxData:= UDINT#10;
	END_IF;
	
///Q2x Inverter Errorcodes and fault factor list name///
	CASE AuxData OF
///Error codes that is not a part of the Drive///		
0:				ErrorCodeInfo:=	'No error'	;
				ErrorCodeDrive:=	'No error'	;
0010	:		ErrorCodeInfo:=	'Drive is Disconnected on the EtherCAT'	;
				ErrorCodeDrive:=	'Bus'	;
0020	:		ErrorCodeInfo:=	'Error in EC_CoESDORead'	;
				ErrorCodeDrive:=CONCAT(		CONCAT('ErrorID: ', (WORD_TO_STRING( ErrorID))), 
																CONCAT('/ AbortCode: ', (DWORD_TO_STRING( ReadSDO.AbortCode))))	;		
