StructuredTextModel.Text:
(* 
This FB referrs to all Conveyors action in Auto mode as well as Manual Mode
	Created: 13/12/2021 Andreas Mihaila
	Tested: 16/12/2021 Andreas Mihaila
	Status: Big IF condition needed to be implemented (Manual Mode and Auto Mode); working properly
*)

IF Status_Machine_Manual_Mode  THEN										// Jog the machine forward
	
	IO_DQ_Conveyor_1 := IO_DI_Panel_Start;
	IO_DQ_Conveyor_2 :=IO_DI_Panel_Start;
	IO_DQ_Entry_Conveyor_1 := IO_DI_Panel_Start;
	IO_DQ_Entry_Conveyor_2 := IO_DI_Panel_Start;

ELSIF Status_Machine_Auto_Mode THEN										// Machine in Auto Mode with one click
	
	IF IO_DI_Panel_Start THEN															// Set Condition
	
		IO_DQ_Conveyor_1 :=  TRUE;
		IO_DQ_Conveyor_2 := TRUE;
		IO_DQ_Entry_Conveyor_1 := TRUE;
		IO_DQ_Entry_Conveyor_2 := TRUE;
		
	END_IF;
	
	 IF NOT IO_DI_Panel_Stop OR Status_Machine_Error THEN		// Reset Condition --> Dominant
		 
		IO_DQ_Conveyor_1 :=  FALSE;
		IO_DQ_Conveyor_2 := FALSE;
		IO_DQ_Entry_Conveyor_1 := FALSE;
		IO_DQ_Entry_Conveyor_2 := FALSE;
		
	 END_IF;
	 
END_IF;
