StructuredTextModel.Text:
(* 
This FB referrs to the entire Panel but without any function: Lights logic is here only implemented
	Created: 11/12/2021 Andreas Mihaila
	Tested: 11/12/2021 Andreas Mihaila
	Status: Working according to requirements
*)

// Input Mapping

DI_Start_Button := IO_DI_Panel_Start;
DI_Stop_Button := IO_DI_Panel_Stop;
DI_Reset_Button := IO_DI_Panel_Reset;
DI_Emergency_Button := IO_DI_Panel_Emergency_Stop;

// Output Mapping

IO_DQ_Panel_Start_Light := DQ_Start_Light;
IO_DQ_Panel_Stop_Light := DQ_Stop_Light;
IO_DQ_Panel_Reset_Light := DQ_Reset_Light;

// Logic Lights

// Start Light

IF DI_Start_Button THEN
	DQ_Start_Light := TRUE;
END_IF;

IF NOT DI_Stop_Button OR NOT DI_Emergency_Button OR DQ_Reset_Light THEN
	DQ_Start_Light := FALSE;
END_IF;

// Reset Light

IF DI_Reset_Button THEN
	DQ_Reset_Light := FALSE;
END_IF;

IF NOT DI_Emergency_Button THEN
	DQ_Reset_Light := TRUE;
END_IF;

// Stop Light

IF DQ_Start_Light AND NOT DQ_Reset_Light
	THEN DQ_Stop_Light := FALSE;
END_IF;

IF NOT DI_Emergency_Button OR NOT DI_Stop_Button
	THEN DQ_Stop_Light := TRUE;
END_IF;
